import React, { useEffect, useState } from 'react'
import axios from 'axios'

interface PokemonImageProps {
  pokemonName: string
}

const PokemonImage: React.FC<PokemonImageProps> = ({ pokemonName }) => {
  const [imageUrl, setImageUrl] = useState<string>('')

  useEffect(() => {
    const fetchPokemonData = async () => {
      try {
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon?limit=100000&offset=0${pokemonName}`,
        )
        const { sprites } = response.data
        const frontDefaultImageUrl: string = sprites.front_default
        setImageUrl(frontDefaultImageUrl)
      } catch (error) {
        console.log(error)
      }
    }

    fetchPokemonData()
  }, [pokemonName])

  return <div>{imageUrl && <img src={imageUrl} alt={pokemonName} />}</div>
}

export default PokemonImage
